import React, { Component } from 'react'
// import insta from './logos/amazon_logo.png';
import SocialButton from './Amazon/Social'
// import google from './logos/google_logo.png'
import UserCard from './Amazon/userCard'
import axios from 'axios';

const image = {





logo: {
  position: 'absolute',
  top: '15px',
  left: '30px',
  cursor: 'pointer',
},
logo1: {
  position: 'absolute',
  top: '15px',
  left: '155px', 
  cursor: 'pointer',
},

logo2: {
  position: 'absolute',
  top: '15px',
  left: '285px', 
  cursor: 'pointer',
},
logo3: {
  position: 'absolute',
  top: '15px',
  left: '415px',
  cursor: 'pointer',
},
logo4: {
  position: 'absolute',
  top: '15px',
  left: '545px',
  cursor: 'pointer',
},
loginwith: {
  position: 'absolute',
  top: '150px',
  left: '150px',
}
}
export default class Demo extends Component {
  constructor (props) {
    super(props)

    this.state = {
      logged: false,
      user: {},
      currentProvider: ''
    }
    this.nodes = {}

    this.onLoginSuccess = this.onLoginSuccess.bind(this)
    this.onLoginFailure = this.onLoginFailure.bind(this)
    this.onLogoutSuccess = this.onLogoutSuccess.bind(this)
    this.onLogoutFailure = this.onLogoutFailure.bind(this)
    this.logout = this.logout.bind(this)
  }



  setNodeRef (provider, node) {
    if (node) {
      this.nodes[ provider ] = node
    }
  }

  onLoginSuccess (user) {
    console.log(user)
      axios.post('https://sociallog.myshopify.com/admin/customers.json', user)
      .then(response => {
        console.log(response)
      })
      .catch(error => {
        console.log(error)
      })
    this.setState({
      logged: true,

      currentProvider: user._provider,
      user
    })
  }

  onLoginFailure (err) {
    console.error(err)

    this.setState({
      logged: false,
      currentProvider: '',
      user: {}
    })
  }

  onLogoutSuccess () {
    this.setState({
      logged: false,
      currentProvider: '',
      user: {}
    })
  }

  onLogoutFailure (err) {
    console.error(err)
  }

  logout () {
    const { logged, currentProvider } = this.state

    if (logged && currentProvider) {
      this.nodes[currentProvider].props.triggerLogout()
    }
  }

  render () {
    let children

    if (this.state.logged) {
      children = <UserCard user={this.state.user} logout={this.logout} />
    } else {

      children = [
        <SocialButton
          provider='facebook'
          appId="743738122718119"
          onLoginSuccess={this.onLoginSuccess}
          onLoginFailure={this.onLoginFailure}
          onLogoutSuccess={this.onLogoutSuccess}
          getInstance={this.setNodeRef.bind(this, 'facebook')}
          key={'facebook'}
        >
          <img style={image.logo} src="https://i.pinimg.com/originals/58/f4/72/58f4723d8f23906bdcb058604075ad2a.png" title="facebook login" alt="facebook" height="150px" width="150px" />
        </SocialButton>,
        <SocialButton
          provider='google'
          appId='555371152154-faiudnv4deckojvcuqsh2kdpf5obe12u.apps.googleusercontent.com'
          onLoginSuccess={this.onLoginSuccess}
          onLoginFailure={this.onLoginFailure}
          onLogoutSuccess={this.onLogoutSuccess}
          onLogoutFailure={this.onLogoutFailure}
          getInstance={this.setNodeRef.bind(this, 'google')}
          key={'google'}
        >
          <img style={image.logo1} src="https://goanapp.com:3001/static/media/google_logo.52fe2301.png" title="google login" alt="google" height="150px" width="150px" />
        </SocialButton>,
         <SocialButton
         provider='amazon'
         appId='amzn1.application-oa2-client.0623170e36984f82a8b4ed9b1230a1cb'
         onLoginSuccess={this.onLoginSuccess}
         onLoginFailure={this.onLoginFailure}
         onLogoutSuccess={this.onLogoutSuccess}
         getInstance={this.setNodeRef.bind(this, 'amazon')}
         key={'amazon'}
       >
         <img style={image.logo2} src="https://goanapp.com:3001/static/media/amazon_logo.884c4758.png" height="150px" width="150px" alt=""/>
       </SocialButton>,
          <h5 style={image.loginwith}>or login with</h5>

      ]


    }

    return children
  }
}
